mapper.update:
UPDATE TB_DAILY
SET DAILY_TITLE = #{title}, DAILY_BODY = #{content}, DAILY_PATH = #{path}, DAILY_UPDATE = now()
WHERE DAILY_ID = #{dailyId};

UPDATE TB_DAILY_COMMENT
SET DAILY_COMMENT_BODY = #{content}, DAILY_COMMENT_UPDATE = now()
WHERE DAILY_COMMENT_ID = #{commentId} AND DAILY_ID = #{dailyId};

UPDATE TB_DAILY
SET DAILY_VIEW = TB_DAILY.DAILY_VIEW + 1, DAILY_UPDATE = now()
WHERE DAILY_ID = #{dailyId};

UPDATE TB_DAILY
SET DAILY_YN = 'Y', DAILY_DELETE = now()
WHERE DAILY_ID = #{dailyId}
